styles:
.vclTourContainer {
    position: absolute !important;
    background-color: #fff;
    z-index: 999;
    text-align: center;
    font-size: 14px;
    color: #000;
    border-radius: 5px;
    -webkit-box-shadow: inset 0 0 30px 0 rgba(0, 0, 0, .5);
    box-shadow: inset 0 0 30px 0 rgba(0, 0, 0, .5);
  }

.vclTourContainer .vclTourContainerHeader {
  padding: 10px;
  border-bottom: 1px solid #ccc;
  background-color: #ddd;
  border-radius: 5px;
  -webkit-box-shadow: inset 0 0 30px 0 rgba(0, 0, 0, .5);
  box-shadow: inset 0 0 30px 0 rgba(0, 0, 0, .5);
}

.vclTourContainer .vclTourContainerContent {
  padding: 10px;
}

.vclTourContainer .vclTourContainerFooter {
  padding: 10px;
}

.vclTourContainer .vclButton:first {
  margin-left: 0;
}

.vclTourContainer.transformX_50.transformY_100 {
  -webkit-transform: translateX(-50%) translateY(-100%);
  transform: translateX(-50%) translateY(-100%);
}

.vclTourContainer.transformX_50 {
  -webkit-transform: translateX(-50%);
  transform: translateX(-50%);
}

.vclTourContainer.transformY_50 {
  -webkit-transform: translateY(-50%);
  transform: translateY(-50%);
}

.vclTourContainer.transformY_50.transformX_100 {
  -webkit-transform: translateX(-100%) translateY(-50%);
  transform: translateX(-100%) translateY(-50%);
}
